Solution

input_score <- 50
add_extra_credit <- function(score, note) {
  if (note == "None") {
      points_to_add <- 0
  } else if (note == "Low") {
      points_to_add <- 1
  } else {
      points_to_add <- 5
  }

  return(score + points_to_add)
}
add_extra_credit(input_score,"None")

[1] 50

add_extra_credit(input_score,"High")

[1] 55

